mapper.select:
SELECT
count(*) FROM reply
WHERE bbsno = #{bbsno}

mapper.update:
update reply
set content =
#{content}
where rnum = #{rnum}

mapper.select:
select
count(*) from reply
where bbsno = #{bbsno}

	select rnum, content, to_char(regdate,'yyyy-mm-dd') regdate,
	id, bbsno,
	r
	from(
	select rnum, content, regdate, id, bbsno, rownum r
	from(
	select
	rnum, content, regdate, id, bbsno
	from reply
	where bbsno = #{bbsno}
	order by rnum desc
	)

)where r>= #{sno} and r<= #{eno}
